value.css:
:root {
  background: #f0f0f0;
  padding: 40px;
  --theme-color: #8fcdc4;
}

#container {
  width: 400px;
  margin: 0 auto;
}

.header {
  background: #8fcdc4;
  color: white;
  font-weight: bold;
  padding: 10px;
}
.card {
  margin-top: 10px;
}

.card {
  background: white;
  padding: 10px;
  display: flex;
  box-shadow: 0px 0px 2px rgba(0, 0, 0, 0.3);
}

.card .img-wrapper {
  background: red;
  flex: 0 0 40%;
}

img {
  flex: 0 0 40%;
  /*  search with img flex-basis  */
  width: 0%;
  object-fit: cover;
}

.card-wrapper {
  flex: 1 1 auto;
  padding: 8px;
  box-sizing: border-box;
}

.card-wrapper p,
.card-wrapper h5 {
  margin: 0px;
}

.card-date {
  text-align: right;
}

.card-tags {
  color: var(--theme-color);
}
